import { useState,useCallback } from 'react'
import { create } from 'ipfs-http-client'
import React from 'react';
import "./Footer.css"
import useEth from "../contexts/EthContext/useEth";
import { Buffer } from 'buffer';
// const client2= create('/ip4/127.0.0.1/tcp/5001');

import {TiChevronLeftOutline, TiChevronRightOutline} from 'react-icons/ti';

const projectId = '2L2d01In1I9OFbre81IirWt0szw';
const projectSecret = '7c43815b40bc5ae32c34ad9d6db87dad';
const auth = 'Basic ' + Buffer.from(projectId + ':' + projectSecret).toString('base64');
var minturl=''


const client2 = create({
  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
      authorization: auth,
  },
});


let ipfsurl = ""
let authenticaiton1=""
let authenticaiton2=""
var newjson=[]
var songs=""


function Footer() {  
  const [fileUrl, updateFileUrl] = useState("");
  const{state: { contract,accounts,web3 } } = useEth();
  const[inputs, setInputs]
   =useState({name:'',number:'',date:'',institution:'',usage:'',location:'',dgrade:'',guardian:'',education:'',awards:'',relationship:'',member:'',submit:'',duration:'',severe:'',work:''});
  const {name ,number,date,institution,usage,location,dgrade,guardian,education,awards,relationship,member,submit,duration,severe,work} =inputs;




async function onChange2(e)
{
  const {value,name} =e.target
