import React, { useEffect } from 'react';
import './Profile.css'
import useEth from '../contexts/EthContext/useEth';
import { useState } from 'react';
import {TiChevronLeftOutline, TiChevronRightOutline} from 'react-icons/ti';
import { useRef } from 'react';


function Profile() 
{    
  var axios = require("axios").default;

  const [onChainUrl, updateonChainUrl] = useState("");
  const [imageUrl, updateImageUrl] = useState("");
  const [imageUrl2, updateImageUrl2] = useState("");
  const [value, setValue] = useState("");
  const [isexist,setExist] = useState(false);  
  const [swap,setswap]=useState(true);
  const{state: { contract, accounts,web3 } } = useEth();



  let TIME= 15;

  const intToString = (num) => {
    return String(num).padStart(2, "0");
  };

  const Timer = ({ss}) => {
    const SS = ss ? parseInt(ss) : 0;

    const count = useRef(SS);
    const interval = useRef(null);

    const [second, setSecond] = useState(intToString(SS));

    useEffect(() => {
      interval.current = setInterval(() => {
        count.current -= 1;

        setSecond(intToString((count.current % 60)));
      }, 1000);
    }, []);

    useEffect(() => {
      if (count.current <= 0) {
        clearInterval(interval.current);
        if(swap==true)
        {
          setswap(false);
          getqr2();
        }
        else{
          setswap(true);
          getqr();
        }

      }
    }, [second]);

    return (
      <div>
        {second}
      </div>
    );
  };






const MAX_VISIBILITY = 3;

const Card = () => (

  <div>
