import React, { useReducer, useCallback, useEffect, useState } from "react";
import Web3 from "web3";
import EthContext from "./EthContext";
import { reducer, actions, initialState } from "./state";
import WalletConnectProvdier from "@walletconnect/web3-provider";

var newjson=[]
var imgjson=[];


export function EthProvider({ children }) {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [proset,setProvider]=useState(false);


 const provider = new WalletConnectProvdier({

  rpc: {
    5:'https://goerli.infura.io/v3/ff2d998f9cdf4be29197ce3ffb727d89'

  },
  infuraId: "ff2d998f9cdf4be29197ce3ffb727d89",


 qrcodeModalOptions: {
   desktopLinks: [
     'ledger',
     'tokenary',
     'wallet',
     'wallet 3',
     'secuX',
     'ambire',
     'wallet3',
     'apolloX',
     'zerion',
     'sequence',
     'punkWallet',
     'kryptoGO',
     'nft',
     'riceWallet',
     'vision',
     'keyring'
   ],
   mobileLinks: [
     "rainbow",
     "metamask",
     "argent",
     "trust",
     "imtoken",
     "pillar",

   ],}});


  const init = useCallback(
    async artifact => {
      if (artifact) {
        // const web3 = new Web3(Web3.givenProvider || "ws://127.0.0.1:7545");

              console.log(provider)
              const web3 = new Web3(provider);

  await provider.enable();
              // Subscribe to accounts change
provider.on("accountsChanged", (accounts) => {
  console.log(accounts);
});

// Subscribe to chainId change
provider.on("chainChanged", (chainId) => {
  console.log(chainId);
});

// Subscribe to session disconnection
provider.on("disconnect", (code,reason) => {
  console.log(code, reason);
});



        // const web3 = new Web3(Web3.givenProvider || "ws://goerli.infura.io/v3/ff2d998f9cdf4be29197ce3ffb727d89");
        const accounts = await web3.eth.getAccounts();
        const networkID = await web3.eth.net.getId();

        const { abi } = artifact;
        let address, contract;




        try {
          address = artifact.networks[networkID].address;
          contract = new web3.eth.Contract(abi, address);




          for(let i=0; i<1; i++)
    {
      //조건은 토큰아이디 or block size-1해서 제한해야함
      //지금 다른계정이 업로드한 노래는 반영을 못하는 구조임.
     try{
      const value = await contract.methods.tokenURI(i).call();

      const response = await fetch(value);
      if(!response.ok)
      throw new Error(response.statusText);
      const json = await response.json();
      //이 json을 넘겨주면된다!
      newjson.push(json);
      //여기서 던져줘야할듯..ㅋㅋ
     }
     catch (err) {


    }

    }



        } catch (err) {
          console.error(err);
          console.log("에러위치");
        }
        dispatch({
          type: actions.init,
          data: { artifact, web3, accounts, networkID, contract,newjson,provider}
        });
      }
    }, []);





  useEffect(() => {
    const tryInit = async () => {
      try {
        const artifact = require("../../contracts/ArtGrowNFT.json");
        init(artifact);
      } catch (err) {
        console.error(err);
      }
    };

    tryInit();
  }, [init]);



  useEffect(() => {
    const events = ["chainChanged", "accountsChanged"];
    const handleChange = () => {
      init(state.artifact);

    };
    events.forEach(e => window.ethereum.on(e, handleChange));
    return () => {
      events.forEach(e => window.ethereum.removeListener(e, handleChange));
    };
  }, [init,state.artifact]);



async function Provide()
{

  console.log(provider)

  await provider.enable();
}
  return (

      <EthContext.Provider value={{
      state,
      dispatch
    }}>

      {children}
          {/* {!proset&&<div style={{display:'flex',gridRow:'5/6',gridColumn:'1/3'}}>
    <button onClick={() => {Provide();  setProvider(true) } }>지갑연결</button>
  </div>}
       */}

    </EthContext.Provider>


  );
}


export default EthProvider;
