import React from "react";
import {useEffect,useState} from 'react'
import "./NavBar.css";
import metadata from "../data/metadata.json";
import { Link } from 'react-router-dom';
import {Button} from './Button';
import {
    EthereumClient,
    modalConnectors,
    walletConnectProvider,
  } from "@web3modal/ethereum";
  import { Web3Button } from "@web3modal/react";

  import { Web3Modal } from "@web3modal/react";
  import { configureChains, createClient, WagmiConfig } from "wagmi";
  import { goerli } from "wagmi";




const NavBar = () => {

    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 960){
            setButton(false)
        }
        else {
            setButton(true);
        }
    };


    const chains =[goerli];

    const {provider} = configureChains(chains,[
      walletConnectProvider({ projectId: "251542da3c8552393e55d6d3b636127e" }),
    ]);

    const wagmiClient = createClient({
      autoConnect: true,
      connectors: modalConnectors({
        projectId: "251542da3c8552393e55d6d3b636127e",
        version: "1", // or "2"
        chains,
      }),
      provider,
    });


    const ethereumClient = new EthereumClient(wagmiClient,chains);


    // SIGNUP버튼이 사이즈가 줄어들면 없어지도록 한다. 
    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize', showButton);



    return (
    <div className="navbar">
            <div className = 'navbar-container'>

            <img src="theirlogo.jpg" width="100" height="50" alt=""/>
            <div className="app-header" >{metadata.appName}</div>

        </div>


            <div className='menu-icon' onClick={handleClick}>
                <i className = {click ? 'fas fa-times' : 'fas fa-bars' } />
            </div>
                <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                <WagmiConfig client={wagmiClient}>

      <Web3Button/>;
        </WagmiConfig>

        <Web3Modal
          projectId="251542da3c8552393e55d6d3b636127e"
          ethereumClient={ethereumClient}
        />

                    <li className='nav-item'>
                        <Link to="/Footer" className='nav-links' onClick = {closeMobileMenu}>
                            Deploy
                        </Link>
                    </li>

                    <li className='nav-item'>
                        <Link to="/Profile" className='nav-links' onClick = {closeMobileMenu}>
                            Profile
                        </Link>
                    </li>
                </ul>
                {/* {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>} */}



    </div>
    );
};

export default NavBar;
