import { useState,useCallback } from 'react'
import { create } from 'ipfs-http-client'
import React from 'react';
import "./Footer.css"
import useEth from "../contexts/EthContext/useEth";
import { Buffer } from 'buffer';
// const client2= create('/ip4/127.0.0.1/tcp/5001');


const projectId = '2L2d01In1I9OFbre81IirWt0szw';
const projectSecret = '7c43815b40bc5ae32c34ad9d6db87dad';
const auth = 'Basic ' + Buffer.from(projectId + ':' + projectSecret).toString('base64');
var minturl=''


const client2 = create({
  host: 'ipfs.infura.io',
  port: 5001,
  protocol: 'https',
  headers: {
      authorization: auth,
  },
});


let ipfsurl = ""
let authenticaiton1=""
let authenticaiton2=""
var newjson=[]
var songs=""


function Footer() {  
  const [fileUrl, updateFileUrl] = useState("");
  const{state: { contract,accounts,web3 } } = useEth();
  const[inputs, setInputs] =useState({name:'',number:'',date:'',institution:'',disorder:'',usage:'',lotation:''});
  const {name ,number,date,institution,disorder,usage,location} =inputs;





async function onChange2(e)
{
  const {value,name} =e.target
    setInputs({
      ...inputs, // 기존의 input 객체를 복사한 뒤
      [name]: value // name 키를 가진 값을 value 로 설정
    });
}

async function onChangeprofile1(e) {
  const file = e.target.files[0]


  try {

    const added = await client2.add(file)
    console.log(added)
    // const url = `http://ipfs.io/ipfs/${added.path}`

    const url = `https://authenticaiton.infura-ipfs.io/ipfs/${added.path}`
    authenticaiton1 = url
    console.log(url)
    updateFileUrl(url)

  } catch (error) {
    console.log('Error uploading file: ', error)
  } 
}

async function onChangeprofile2(e) {
  const file = e.target.files[0]
  try {

    const added = await client2.add(file)
    console.log(added)
    // const url = `http://ipfs.io/ipfs/${added.path}`

    const url = `https://authenticaiton.infura-ipfs.io/ipfs/${added.path}`
    authenticaiton2= url
    updateFileUrl(url)
    console.log(url)
  } catch (error) {
    console.log('Error uploading file: ', error)
  } 
}



async function authentify(e)
{
  try {
  let json = `{"url":"${ipfsurl}","name":"${name}","number":"${number}",
  "date":"${date}","institution":"${institution}","disorder":"${disorder}","usage":"${usage}",,"location":"${location}",
  "블록체인에 기록되는 정보입니다.",
  "links":{
    "images":[

      {
        "url":"${authenticaiton1}"
      }
      ,
      {
        "url":"${authenticaiton2}"
      }
    ]
  },"attributes":[{"trait_type": "Unknown","value": "Unknown"}]
}`;


  const added = await client2.add(json)
  const url = `https://authenticaiton.infura-ipfs.io/ipfs/${added.path}`
  minturl=url;
  initTransaction(accounts)


  // const output = await contract.methods.safeMint(accounts[0],url).send({from:accounts[0]});
  // setValue(output);

  // console.log(output)

} 
catch (error) {  
}
}



const initTransaction = useCallback(
  async accounts=>
  {
      if(accounts)
{ 
  const chainid = await web3.eth.getChainId();    

  var writerAddr=accounts[0];
  const data=await contract.methods.safeMint(accounts[0],minturl).encodeABI();
  const txCount = await web3.eth.getTransactionCount(accounts[0]);
  const nonce =web3.utils.toHex(txCount);
  const gasprice = await web3.eth.getGasPrice();
  const gasPrice = Math.round(Number(gasprice) + Number(gasprice / 3));

  let txObject={
      from:writerAddr,
      nonce:nonce,
      to:contract._address,
      data :data,
      gasLimit:web3.utils.toHex(3000000),
      gasPrice:web3.utils.toHex(gasPrice),
      chainId:chainid
  }
  var trhash=await web3.eth.accounts.signTransaction(txObject);
  const hash=web3.eth.sendSignedTransaction(trhash.rawTransaction);
  console.log(hash); 
}   
},[contract, web3])


  const read = async () => {

    var num= await web3.eth.getBlockNumber();
    var latestToken=''
    let i='';
    try {
      for(i=10; i<num; i++)
    {
      //조건은 토큰아이디 or block size-1해서 제한해야함
      const value = await contract.methods.tokenURI(i).call({ from: accounts[0] });
      console.log(value)
      const response = await fetch(value);
      if(!response.ok)
      throw new Error(response.statusText);  
      const json = await response.json();
      //이 json을 넘겨주면된다!
      newjson.push(json);
      latestToken=await contract.methods.ownerOf(i).arguments;

    }

    } catch (error) {
      latestToken=await contract.methods.ownerOf(i-1).arguments;
      console.log(latestToken[0]); 
    }
    songs=JSON.stringify(newjson);
    console.log(songs)

  };


  return (

  <div className='Deploys' >



<div className='Procedure' >



   <div className='container'>

    <div className='ProDetail2'>
    <h1>정보를 입력해주세요</h1>


